opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.StartPoint = [0.0843065411551075 0.739014598693051 0.438744359656398];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

